Source code in <code>Akatosh\resource.py</code> <pre><code>class Resource:
def __init__(
self,
capacity: int | float | Callable,
initial_amount: int | float | Callable | None = None,
label: str | None = None,
) -&gt; None:
"""Resource is a class that represents a resource with capacity and amount, any object can use this resource by calling distribute() and return by calling collect() methods.
        Args:
            capacity (int | float | Callable): the capacity of the resource.
            initial_amount (int | float | Callable | None, optional): the initial amount of the resource. Defaults to capacity.
            label (str | None, optional): short description of the resource. Defaults to None.
        Raises:
            ValueError: raise if initial amount is greater than capacity.
        """
if callable(capacity):
self._capacity = capacity()
else:
self._capacity = capacity
if initial_amount:
if callable(initial_amount):
if initial_amount() &gt; self.capacity:
raise ValueError("Initial amount is greater than capacity.")
else:
self._amount = initial_amount()
else:
if initial_amount &gt; self.capacity:
raise ValueError("Initial amount is greater than capacity.")
else:
self._amount = initial_amount
else:
self._amount = self.capacity
self._label = label
self._user_records: List[Tuple[object, int | float]] = list() # (user, amount) tracking the usage of individual user
self._usage_records: List[Tuple[int | float, int | float]] = list() # (time, amount) tracking the usage of the resource over time
def get(self, amount: int | float) -&gt; None:
"""Get the amount of resource from the resource.
        Args:
            amount (int | float): the amount of resource to get.
        Raises:
            ValueError: raise if amount is greater than the current available amount of resource.
        """        
if amount &gt; self.amount:
raise ValueError(f"Not enough amount in Resource {self.label}.")
else:
self._amount -= amount
self.usage_records.append((Mundus.now, self.amount))
def put(self, amount: int | float) -&gt; None:
"""Put the amount of resource back to the resource.
        Args:
            amount (int | float): the amount of resource to put.
        Raises:
            ValueError: raise if amount is greater than the current used amount of resource.
        """        
if amount &gt; self.occupied:
raise ValueError(f"Not enough capacity in Resource {self.label}.")
else:
self._amount += amount
self.usage_records.append((Mundus.now, self.amount))
def distribute(self, user: object, amount: int | float) -&gt; None:
"""Distribute the amount of resource to the user.
        Args:
            user (object): the user of the resource.
            amount (int | float): the amount of resource to distribute.
        Raises:
            ValueError: raise if amount is greater than the current available amount of resource.
        """        
if amount &gt; self.amount:
raise ValueError(f"Not enough amount in Resource {self.label}.")
else:
self._amount -= amount
if user in self.users:
for index, record in enumerate(self.user_records):
if record[0] is user:
self.user_records[index] = (user, record[1] + amount)
break
else:
self.user_records.append((user, amount))
self.usage_records.append((Mundus.now, self.amount))
logger.debug(f"Resource {self.label} distributed {amount} to {user}.")
def collect(self, user: object, amount: int | float | None = None) -&gt; None:
"""Collect the amount of resource from the user.
        Args:
            user (object): the user of the resource.
            amount (int | float | None, optional): the amount of resource to collect. Defaults to None means collecting all resource used by the user.
        Raises:
            ValueError: raise if user is not using the resource or amount is greater than the current used amount of resource.
        """        
if user not in self.users:
raise ValueError(f"User {user} is not using Resource {self.label}.")
if amount is None:
for index, record in enumerate(self.user_records):
if record[0] is user:
self._amount += record[1]
self.usage_records.append((Mundus.now, self.amount))
logger.debug(
f"Resource {self.label} collected {record[1]} from {user}."
)
break
else:
for index, record in enumerate(self.user_records):
if record[0] is user:
if record[1] &lt; amount:
raise ValueError(
f"{user} occupied {record[1]} of Resource {self.label}, less than {amount}."
)
else:
self.user_records[index] = (user, record[1] - amount)
if self.user_records[index][1] == 0:
self.user_records.pop(index)
self._amount += amount
self.usage_records.append((Mundus.now, self.amount))
logger.debug(
f"Resource {self.label} collected {amount} from {user}."
)
break
def usage(self, duration: int | float | Callable | None = None):
"""Return the usage of the resource in the duration.
        Args:
            duration (int | float | Callable | None, optional): the duration to trace back in time. Defaults to None.
        """        
if duration:
if callable(duration):
after = Mundus.now - duration()
else:
after = Mundus.now - duration
if after &lt; 0:
after = 0
usage_records = [
usage_record
for usage_record in self.usage_records
if usage_record[0] &gt;= after
]
if len(usage_records) == 0:
return 1 - (self.amount / self.capacity)
else:
if usage_records[-1][0] - after == 0:
return 1 - (usage_records[-1][1] / self.capacity)
else:
weighted_overall_amount = 0
for index, record in enumerate(usage_records):
if index == 0:
weighted_overall_amount += record[1] * (record[0] - after)
elif index == len(usage_records) - 1:
weighted_overall_amount += record[1] * (
Mundus.now - usage_records[index - 1][0]
)
else:
weighted_overall_amount += record[1] * (
record[0] - usage_records[index - 1][0]
)
return 1 - (
weighted_overall_amount / (usage_records[-1][0] - after)
)
else:
return 1 - (self.amount / self.capacity)
@property
def amount(self) -&gt; int | float:
"""Return the current available amount of the resource."""
return self._amount
@property
def capacity(self) -&gt; int | float:
"""Return the capacity of the resource."""
return self._capacity
@property
def occupied(self) -&gt; int | float:
"""Return the current occupied amount of the resource."""
return self.capacity - self.amount
@property
def label(self) -&gt; str | None:
"""Return the label of the resource."""
return self._label
@property
def user_records(self) -&gt; List[Tuple[object, int | float]]:
"""Return the usage records of the resource per user."""
return self._user_records
@property
def usage_records(self) -&gt; List[Tuple[int | float, int | float]]:
"""Return the usage records of the resource, for calculating the usage of the resource in a duration."""
return self._usage_records
@property
def users(self) -&gt; List[object]:
"""Return the users of the resource."""
return [record[0] for record in self._user_records]
</code></pre>

Source code in <code>Akatosh\universe.py</code> <pre><code>class Universe:
def __init__(self) -&gt; None:
"""The Simulation Universe."""
self._resolution = (
1  # the resolution of the time. 1 means minimum time unit is 0.1 second.
)
self._now: int | float = -1  # the current time of the simulated universe.
self._future_events: List[Event] = list()  # the future events queue.
self._current_events: List[Event] = list()  # the current events queue.
self._past_events: List[Event] = list()  # the past events queue.
self._alduin: bool = False  # trigger to stop the simulation.
self.set_logger(logging.ERROR)  # set the default logger level to ERROR.
async def akatosh(self, till: int | float | None = None):
"""Akatosh is the god of time in the Elder Scrolls universe. This method is the core of the simulation.
        Args:
            till (int | float | None, optional): the end time of the simulated universe. Defaults to None.
        Raises:
            RuntimeError: raise if the event is in unknown state or being placed in wrong event queue.
        """
while len(self.future_events) != 0:
# stop simulation if world eater alduin is triggered.
if self.alduin:
return
# update the current time of the simulated universe.
valid_future_events = [
event
for event in self.future_events
if event.state == State.ACTIVE or event.state == State.INACTIVE
]
if len(valid_future_events) == 0:
return
_time = min(event.at for event in valid_future_events)
if self.now &lt; _time:
self._now = _time
# stop simulation if the end time is reached.
if till is not None:
if self.now &gt; till:
return
logger.debug(f"Time: {self.now}")
logger.debug(
f"Future active events: {[(event.at, event.label) for event in self.future_events]}"
)
self.organise_events()
logger.debug(
f"Current events: {[(event.priority, event.label) for event in self.current_events]}"
)
await self.execute_current_events()
def organise_events(self):
"""Organise the events in the future events queue. Valid event will be moved to the current events queue. Non-valid event will be moved to the past events queue.
        Raises:
            RuntimeError: raise if the event is in unknown state or being placed in wrong event queue.
        """
for event in self.future_events[:]:
if event.at &lt;= self.now:
if event.state == State.ACTIVE or event.state == State.INACTIVE:
logger.debug(f"Event {event.label} is triggered.")
self.future_events.remove(event)
self.current_events.append(event)
elif event.state == State.CANCELED:
raise RuntimeError(
f"Event {event.label} is canceled but inside future events queue."
)
elif event.state == State.ENDED:
raise RuntimeError(
f"Event {event.label} is ended but inside future events queue."
)
else:
raise RuntimeError(f"Event {event.label} is in unknown state.")
async def execute_current_events(self):
"""Execute the current events queue based on priority. The event with the lowest priority will be executed first. The event with the same priority will be executed concurrently.
        """
while (
len([event for event in self.current_events if event.state == State.ACTIVE])
!= 0
):
priority = min(
event.priority
for event in self.current_events
if event.state == State.ACTIVE
)
logger.debug(
f"Current Priority: {priority}, Executing events: {[event.label for event in self.current_events if event.priority == priority]}"
)
await asyncio.gather(
*[
event._perform()
for event in self.current_events
if event.priority == priority
]
)
def simulate(self, till: int | float | None = None):
"""Start the simulation."""
asyncio.run(self.akatosh(till))
def set_logger(self, level: int):
"""Set the logger level."""
logger.setLevel(level)
@property
def resolution(self) -&gt; int:
"""Return  the resolution of the time."""
return self._resolution
@property
def now(self) -&gt; int | float:
"""Return the current time of the simulated universe."""
return self._now
@property
def future_events(self) -&gt; List[Event]:
"""Return the future events queue."""
return self._future_events
@property
def current_events(self) -&gt; List[Event]:
"""Return the current events queue."""
return self._current_events
@property
def past_events(self) -&gt; List[Event]:
"""Return the past events queue."""
return self._past_events
@property
def alduin(self) -&gt; bool:
"""Return the trigger to stop the simulation."""
return self._alduin
@alduin.setter
def alduin(self, value: bool):
"""Set the trigger to stop the simulation."""
self._alduin = value
